if(jQuery('.hide').slideUp('slow'))
{
$hide.stop().slideDown('slow');
//$('.hide').css('display', 'none');

}
else
{
if($hide.stop().slideDown('slow'))
{
jQuery('.hide').slideUp('slow');
//jQuery('.hide').show() ;
}
}

