}
//Fin bacilloscopie

//Debut supplementation en vitamine A
if ((getValue('vitaminea_1.value') == '')) {
setValue('vitaminea.value', '');
} else {
setValue('vitaminea.value', '86339');
}
if ((getValue('fer_1.value') == '')) {
setValue('fer.value', '');
} else {
setValue('fer.value', '5843');
}
if ((getValue('iode_1.value') == '')) {
