var calculateBmi = function(wt, ht) {
var bmi = null
if (wt && ht) {
bmi = wt / ((ht/100) * (ht/100));
}
return bmi ;
}
var calculatePbpc = function(pb, pc) {
var pbpc = null;
if (pb && pc) {
pbpc = pb/pc;
}
return pbpc;
}
// functions to handle updating the bmi when in ENTER mode
var updateBmi = function() {
var wt = htmlForm.getValueIfLegal('weight.value');
var ht = htmlForm.getValueIfLegal('height.value');

var bmi = calculateBmi(wt, ht);

if (bmi == null || isNaN(bmi)) {
jQuery('#calculated-bmi-wrapper').hide();
} else {
jQuery('#no-calculated-bmi').hide();
jQuery('#calculated-bmi-wrapper').show();
jQuery('#calculated-bmi').html(bmi.toFixed(1));
jQuery('#hidden-calculated-bmi').val(bmi.toFixed(1));
}
};

var updatePbpc = function() {
var pb = htmlForm.getValueIfLegal('pb.value');
var pc = htmlForm.getValueIfLegal('pc.value');

var pbpc = calculatePbpc(pb, pc);

if (pbpc == null || isNaN(pbpc)) {
jQuery('#calculated-pbpc-wrapper').hide();
} else {
jQuery('#no-calculated-pbpc').hide();
jQuery('#calculated-pbpc-wrapper').show();
jQuery('#calculated-pbpc').html(pbpc.toFixed(1));
jQuery('#hidden-calculated-bmi').val(pbpc.toFixed(1));
}
};

