jQuery('htmlform').each(function(index, form) {
jQuery(form).find('#calculated-bmi-continue').hide();
jQuery(form).find('#no-calculated-bmi').hide();

var wt = jQuery(form).find('#weight').find('.value').text();
var ht = jQuery(form).find('#height').find('.value').text();

var bmi = calculateBmi(wt, ht);

if (bmi != null && !isNaN(bmi)) {
jQuery(form).find('#calculated-bmi-wrapper').show();
jQuery(form).find('#calculated-bmi').html(bmi.toFixed(1));
jQuery(form).find('#hidden-calculated-bmi').val(bmi.toFixed(1));
}
});

jQuery('#calculated-bmi-wrapper').each(function(){
jQuery('#calculated-bmi-wrapper').hide();
getField('weight.value').change(updateBmi)
getField('height.value').change(updateBmi);
updateBmi();
});
// CALCULET PB / PC
jQuery('htmlform').each(function(index, form) {
jQuery(form).find('#calculated-pbpc-continue').hide();
jQuery(form).find('#no-calculated-pbpc').hide();

var pb = jQuery(form).find('#pb').find('.value').text();
var pc = jQuery(form).find('#pc').find('.value').text();

var pbpc = calculatePbpc(pb, pc);

if (pbpc != null && !isNaN(pbpc)) {
jQuery(form).find('#calculated-pbpc-wrapper').show();
jQuery(form).find('#calculated-pbpc').html(pbpc.toFixed(1));
jQuery(form).find('#hidden-calculated-pbpc').val(pbpc.toFixed(1));
}
});

jQuery('#calculated-pbpc-wrapper').each(function(){
jQuery('#calculated-pbpc-wrapper').hide();
getField('pb.value').change(updatePbpc)
getField('pc.value').change(updatePbpc);
updatePbpc();
});
