var labelsList = "\n";
var valid = true;

jQuery('.field-error').each(function() {
	if (!jQuery(this).is(':empty')){
		var parent = jQuery(this).parent();
		var label = parent[0].firstChild.data;

		if (label !== undefined) {
			labelsList += label + "\n";
		}
		valid = false;
	}
});

if (!valid) {
	alert("La sauvegarde a échoué à cause d'une erreur dans la fiche, veuillez corriger les champs avec erreur" + labelsList);
}
return valid;
});

jQuery(function() {

	jQuery('.titre').each(function() {
		var m =jQuery(this).find(".master").attr("id");
		if(getValue(''+m+'.value')>0)
		{
			jQuery( this.getAttribute( 'ch-target' ) ).show();
		}
		else{
			if(getValue(''+m+'.value') == null){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
			}
		}
		if(getValue(''+m+'.value') == ''){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
		}
	});

	jQuery( '.titre' ).on( 'change', function() {

		var m =jQuery(this).find(".master").attr("id");
		if(getValue(''+m+'.value')>0 || getValue(''+m+'.value') != null)
		{
			jQuery( this.getAttribute( 'ch-target' ) ).show();
		}
		else{
			if(getValue(''+m+'.value') == null){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
			}

		}
		if(getValue(''+m+'.value') == ''){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
		}


	});

});

