htmlform.script:
   jQuery(function() {
		jQuery( '.toggle-button' ).on( 'click', function() {
			// À décommenter si tu veux masquer toutes les autres div
			jQuery( '.togglable' ).not( this.getAttribute( 'data-target' ) ).hide( 'slow' );

			jQuery( this.getAttribute( 'data-target' ) ).toggle( 'slow' );
		});
	});

	  jQuery(function() {
       jQuery( '.titre' ).on( 'change', function() {
		jQuery( this.getAttribute( 'ch-target' ) ).show();

        });
    });

	 beforeSubmit.push(function() {

	 //Debut bacilloscopie
	if ((getValue('bacidate1.value') == '') && (getValue('bacipoids1.value') == '') && (getValue('bacilloscopie1.value') != '703') && (getValue('bacilloscopie1.value') != '664')) {
       setValue('bacimois1.value', '');
    } else {
	    setValue('bacimois1.value', '0');
    }
	if ((getValue('bacidate2.value') == '') && (getValue('bacipoids2.value') == '') && (getValue('bacilloscopie2.value') != '703') && (getValue('bacilloscopie2.value') != '664')) {
       setValue('bacimois2.value', '');
    } else {
	    setValue('bacimois2.value', '2');
    }
	if ((getValue('bacidate3.value') == '') && (getValue('bacipoids3.value') == '') && (getValue('bacilloscopie3.value') != '703') && (getValue('bacilloscopie3.value') != '664')) {
       setValue('bacimois3.value', '');
    } else {
	    setValue('bacimois3.value', '3');
    }
	if ((getValue('bacidate4.value') == '') && (getValue('bacipoids4.value') == '') && (getValue('bacilloscopie4.value') != '703') && (getValue('bacilloscopie4.value') != '664')) {
       setValue('bacimois4.value', '');
    } else {
	    setValue('bacimois4.value', '5');
    }
	if ((getValue('bacidate5.value') == '') && (getValue('bacipoids5.value') == '') && (getValue('bacilloscopie5.value') != '703') && (getValue('bacilloscopie5.value') != '664')) {
       setValue('bacimois5.value', '');
    } else {
	    setValue('bacimois5.value', 'Fin de rx');
    }
	//Fin bacilloscopie



	 return true;
	});
