htmlform.script:
jQuery(function() {
	jQuery('#onglets').css('display', 'block');
	jQuery('#onglets').click(function(event) {
		var actuel = event.target;
		if (!/li/i.test(actuel.nodeName) || actuel.className.indexOf('actif') > -1) {
			alert(actuel.nodeName)
			return;
		}
		jQuery(actuel).addClass('actif').siblings().removeClass('actif');
		setDisplay();
	});
	function setDisplay() {
		var modeAffichage;
		jQuery('#onglets li').each(function(rang) {
			modeAffichage = jQuery(this).hasClass('actif') ? '' : 'none';
			jQuery('.item').eq(rang).css('display', modeAffichage);
		});
	}
	setDisplay();
});

   jQuery(function() {
        jQuery( '.titre' ).on( 'change', function() {
			//jQuery( this.getAttribute( 'ch-target' ) ).toggle( 'slow' );
			var ma =jQuery(this).find(".master").attr("id");
			if(getValue(''+ma+'.value')>0)
			{
				jQuery( this.getAttribute( 'ch-target' ) ).show();
			}
			else{
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
			}
        });
    });

function validateSccRequiredFields(parentObsId, childObsId) {
	if (getValue('destinationOfTestOrder.value') == 'SCC') {
		var parentObs = getValue(parentObsId + '.value');
		var childObs = getValue(childObsId + '.value');
		if (parentObs != null && parentObs != '' && (childObs == null || childObs == '')) {
			getField(childObsId + '.error').html('Required').show();
			return false;
		}
	}
	getField(childObsId + '.error').html('').show();
	return true;
}

beforeValidation.push(function() {
	return validateSccRequiredFields('idchargeviralqua', 'viralLoadStage');
});

beforeValidation.push(function() {
	return validateSccRequiredFields('idpcr32', 'pcrStage');
});

jQuery(function() {
jQuery('#destinationOfTestOrder').on( 'change', function() {
	if (getValue('destinationOfTestOrder.value') == 'SCC') {
		jQuery('#viralLoadStage').show();
		jQuery('#viralLoadStage:not(:has(.required))').append('*');
		jQuery('#pcrStage').show();
		jQuery('#pcrStage:not(:has(.required))').append('*');
	} else {
		jQuery('#viralLoadStage').hide()
		jQuery('#pcrStage').hide();
	}
});
