htmlform.script:
jQuery(function() {
jQuery( '.toggle-button' ).on( 'click', function() {
// À décommenter si tu veux masquer toutes les autres div
jQuery( '.togglable' ).not( this.getAttribute( 'data-target' ) ).hide( 'slow'
);

jQuery( this.getAttribute( 'data-target' ) ).toggle( 'slow' );
});
});

jQuery(function() {
jQuery('.titre').each(function() {
		var m =jQuery(this).find(".master").attr("id");
		if(getValue(''+m+'.value')>0)
		{
			jQuery( this.getAttribute( 'ch-target' ) ).show();
		}
		else{
			if(getValue(''+m+'.value') == null){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
			}
		}
		if(getValue(''+m+'.value') == ''){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
		}
	});

	jQuery( '.titre' ).on( 'change', function() {

		var m =jQuery(this).find(".master").attr("id");
		if(getValue(''+m+'.value')>0 || getValue(''+m+'.value') != null)
		{
			jQuery( this.getAttribute( 'ch-target' ) ).show();
		}
		else{
			if(getValue(''+m+'.value') == null){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
			}

		}
		if(getValue(''+m+'.value') == ''){
				jQuery( this.getAttribute( 'ch-target' ) ).hide();
		}


	});



});

