group.find("#pasconduit1").find("input[type$='checkbox']").attr("checked",false);
group.find("#pasconduit1").find("input[type$='checkbox']").change();
}
);
group.find("#anormal1").find("input[type$='checkbox']").click(function(){
group.find("#normal1").find("input[type$='checkbox']").attr("checked",false);
group.find("#normal1").find("input[type$='checkbox']").change();
group.find("#pasconduit1").find("input[type$='checkbox']").attr("checked",false);
group.find("#pasconduit1").find("input[type$='checkbox']").change();
}
);
group.find("#pasconduit1").find("input[type$='checkbox']").click(function(){
group.find("#normal1").find("input[type$='checkbox']").attr("checked",false);
group.find("#normal1").find("input[type$='checkbox']").change();
group.find("#anormal1").find("input[type$='checkbox']").attr("checked",false);
group.find("#anormal1").find("input[type$='checkbox']").change();
}
);

group.find("#trigger").find("input[type$='checkbox']").click(function(){
     var res = group.find(".titre").attr("id");
     var idm = group.find(".master").attr("id");

   	if(getValue(''+idm+'.value') == null || getValue(''+idm+'.value') == '')
	{
		group.find("#confirmation").find("input[type$='radio']").attr("checked",false);
		group.find("#confirmation").find("input[type$='radio']").change();
		group.find("#primary").find("input[type$='radio']").attr("checked",false);
		group.find("#primary").find("input[type$='radio']").change();

		jQuery( group.find(".cacher") ).hide();
		var idcacher = '#' + group.find(".cacher").attr("id") + '';
		//alert(idcacher);
		jQuery(''+idcacher+'').hide();
	} 	
 });

 group.find("#trigger").find("input[type$='text']").change(function(){
     var res = group.find(".titre").attr("id");
     var idm = group.find(".master").attr("id");

   	if(getValue(''+idm+'.value') == null || getValue(''+idm+'.value') == '')
	{
		group.find("#confirmation").find("input[type$='radio']").attr("checked",false);
		group.find("#confirmation").find("input[type$='radio']").change();
		group.find("#primary").find("input[type$='radio']").attr("checked",false);
		group.find("#primary").find("input[type$='radio']").change();
		jQuery( group.find(".cacher") ).hide();
		var idcacher = '#' + group.find(".cacher").attr("id") + '';
		jQuery(''+idcacher+'').hide();
	} 	
 });



});
});
