jQuery.fn.cleanWhitespace = function() {
textNodes = this.contents().filter(
function() { return (this.nodeType == 3 &&
!/\S/.test(this.nodeValue)); })
.remove();
return this;
}

jQuery(function() {
jQuery(".section").cleanWhitespace();
});

