jQuery(function() {
jQuery( '.titre' ).on( 'change', function() {
/*jQuery( this.getAttribute(
'ch-target' ) ).show();*/

var m =jQuery(this).find(".master").attr("id");
	if(getValue(''+m+'.value')>0 || getValue(''+m+'.value') != null)
	{
		jQuery( this.getAttribute( 'ch-target' ) ).show();
	}
	else{
		if(getValue(''+m+'.value') == null){
			jQuery( this.getAttribute( 'ch-target' ) ).hide();
		}

	}
	if(getValue(''+m+'.value') == ''){
			jQuery( this.getAttribute( 'ch-target' ) ).hide();
	}


});

jQuery( '#date_cancer_col' ).on( 'change', function() {

	if(getValue('date_cancer_col.value') != '') {
		setValue('cancer_col.value', '151185');
	}
});
jQuery( '#cancer_col' ).on( 'change', function() {

	if(getValue('cancer_col.value') != '151185') {
		setValue('date_cancer_col.value', '');
	}
});

jQuery( '#date_depistage_prostate' ).on( 'change', function() {

	if(getValue('date_depistage_prostate.value') != '') {
		setValue('depistage_prostate.value', '163464');
	}
});

jQuery( '#depistage_prostate' ).on( 'change', function() {

	if(getValue('depistage_prostate.value') != '163464') {
		setValue('date_depistage_prostate.value', '');
	}
});

});

