var calculateBmi = function(wt, ht) {
var bmi = null
if (wt && ht) {
bmi = wt / ((ht/100) * (ht/100));
}
return bmi ;
}
// functions to handle updating the bmi when in ENTER mode
var updateBmi = function() {
var wt = htmlForm.getValueIfLegal('weight.value');
var ht = htmlForm.getValueIfLegal('height.value');

var bmi = calculateBmi(wt, ht);

if (bmi == null || isNaN(bmi)) {
jQuery('#calculated-bmi-wrapper').hide();
} else {
jQuery('#no-calculated-bmi').hide();
jQuery('#calculated-bmi-wrapper').show();
jQuery('#calculated-bmi').html(bmi.toFixed(1));
jQuery('#hidden-calculated-bmi').val(bmi.toFixed(1));
}
};

jQuery(function() {
jQuery(".hide").hide();
jQuery(".lienvisible").click(function() {
// on recupere le lien dans le DOM
var $link = jQuery(this);

// on recupere la balise hide suivant le lien dans le DOM
var $hide = $link.next('.hide');
if(jQuery('.hide').slideUp('slow'))
{
$hide.stop().slideDown('slow');
//$('.hide').css('display', 'none');

}
else
{
if($hide.stop().slideDown('slow'))
{
jQuery('.hide').slideUp('slow');
//jQuery('.hide').show() ;
}
}

// on remonte toutes les balises hide
//$j('.hide').slideUp('slow');
// on stoppe l'animation (slideUp de la ligne precedente)
// on desend notre div
//$hide.stop().slideDown('slow');
});

