var calculateBmi = function(wt, ht) {
var bmi = null
if (wt && ht) {
bmi = wt / ((ht/100) * (ht/100));
}
return bmi ;
}
var calculatePbpc = function(pb, pc) {
var pbpc = null;
if (pb && pc) {
pbpc = pb/pc;
}
return pbpc;
}
// functions to handle updating the bmi when in ENTER mode
var updateBmi = function() {
var wt = htmlForm.getValueIfLegal('weight.value');
var ht = htmlForm.getValueIfLegal('height.value');

var bmi = calculateBmi(wt, ht);

if (bmi == null || isNaN(bmi)) {
jQuery('#calculated-bmi-wrapper').hide();
} else {
jQuery('#no-calculated-bmi').hide();
jQuery('#calculated-bmi-wrapper').show();
jQuery('#calculated-bmi').html(bmi.toFixed(1));
jQuery('#hidden-calculated-bmi').val(bmi.toFixed(1));
}
};

var updatePbpc = function() {
var pb = htmlForm.getValueIfLegal('pb.value');
var pc = htmlForm.getValueIfLegal('pc.value');

var pbpc = calculatePbpc(pb, pc);

if (pbpc == null || isNaN(pbpc)) {
jQuery('#calculated-pbpc-wrapper').hide();
} else {
jQuery('#no-calculated-pbpc').hide();
jQuery('#calculated-pbpc-wrapper').show();
jQuery('#calculated-pbpc').html(pbpc.toFixed(1));
jQuery('#hidden-calculated-bmi').val(pbpc.toFixed(1));
}
};

jQuery(function() {
jQuery(".hide").hide();
jQuery(".lienvisible").click(function() {
// on recupere le lien dans le DOM
var $link = jQuery(this);

// on recupere la balise hide suivant le lien dans le DOM
var $hide = $link.next('.hide');
if(jQuery('.hide').slideUp('slow'))
{
$hide.stop().slideDown('slow');
//$('.hide').css('display', 'none');

}
else
{
if($hide.stop().slideDown('slow'))
{
jQuery('.hide').slideUp('slow');
//jQuery('.hide').show() ;
}
}


});


jQuery('htmlform').each(function(index, form) {
jQuery(form).find('#calculated-bmi-continue').hide();
jQuery(form).find('#no-calculated-bmi').hide();

var wt = jQuery(form).find('#weight').find('.value').text();
var ht = jQuery(form).find('#height').find('.value').text();

var bmi = calculateBmi(wt, ht);

if (bmi != null && !isNaN(bmi)) {
jQuery(form).find('#calculated-bmi-wrapper').show();
jQuery(form).find('#calculated-bmi').html(bmi.toFixed(1));
jQuery(form).find('#hidden-calculated-bmi').val(bmi.toFixed(1));
}
});

jQuery('#calculated-bmi-wrapper').each(function(){
jQuery('#calculated-bmi-wrapper').hide();
getField('weight.value').change(updateBmi)
getField('height.value').change(updateBmi);
updateBmi();
});
// CALCULET PB / PC
jQuery('htmlform').each(function(index, form) {
jQuery(form).find('#calculated-pbpc-continue').hide();
jQuery(form).find('#no-calculated-pbpc').hide();

var pb = jQuery(form).find('#pb').find('.value').text();
var pc = jQuery(form).find('#pc').find('.value').text();

var pbpc = calculatePbpc(pb, pc);

if (pbpc != null && !isNaN(pbpc)) {
jQuery(form).find('#calculated-pbpc-wrapper').show();
jQuery(form).find('#calculated-pbpc').html(pbpc.toFixed(1));
jQuery(form).find('#hidden-calculated-pbpc').val(pbpc.toFixed(1));
}
});

jQuery('#calculated-pbpc-wrapper').each(function(){
jQuery('#calculated-pbpc-wrapper').hide();
getField('pb.value').change(updatePbpc)
getField('pc.value').change(updatePbpc);
updatePbpc();
});

});

